import classnames from 'classnames';

class Button extends React.Component {
    render() {
        let classes = classnames('button', ['button', 'buttonText', 'Oswald']);
        return <div className={classes}>{this.props.text}</div>;
    }
}

class StyledText extends React.Component {
    render() {
        let classes = classnames('title', [this.props.style, 'Oswald', 'noSelect']);
        return <div>
            <div className={classes}>
                {this.props.text}
            </div>
        </div>;
    }
}

module.exports = {StyledText: StyledText, Button: Button};
