import classnames from 'classnames';
var Button = require('wm/renderer/ui/Components').Button;
var StyledText = require('wm/renderer/ui/Components').StyledText;

function ReactUi(){
    ReactDOM.render(
        <div><StartScreen/><EndScreen/></div>,
        document.getElementById('react-content')
    );
}

class StartScreen extends React.Component {
    render() {
        let text = <div>
            <span>{'WINGMOD'}</span>
            <span style={{color: 'red'}}>{'2'}</span>
        </div>;
        return <div>
            <StyledText style={'titleText'} text={text}/>
            <Button text={'START'}/>
        </div>;
    }
}

class EndScreen extends React.Component {
    render() {
        return <div>
            <StyledText style={'titleText'} text={'GAME OVER'}/>
            <StyledText style={'scoreText'} text={'Score data template'}/>
        </div>;
    }
}

module.exports = ReactUi;

